import React, { useEffect, useRef, useState } from 'react';
import { Chart } from 'chart.js/auto';
import { format } from 'date-fns';
import { AdapterDateFns } from 'chartjs-adapter-date-fns';

Chart.register(AdapterDateFns);

const LineGraph = (props) => {
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    setGraphData([...props.lineGraph]);
  }, [props.lineGraph]);

  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    const labels = graphData.map((item) => format(new Date(item.data.timestamp), 'MM/dd/yyyy'));
    const datasets = graphData.map((item) => ({
      label: item.data.indoorTemperature10xF,
      data: graphData.map((item) => item.data.indoorTemperature10xF),
      fill: false,
      borderColor: getRandomColor(),
      tension: 0.1,
    }));

    chartInstance.current = new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: datasets,
      },
      options: {
        maintainAspectRatio: false,
        scales: {
          x: {
            type: 'time',
            time: {
              unit: 'day',
              displayFormats: {
                day: 'MM/DD/YYYY',
              },
            },
            ticks: {
              maxRotation: 0,
              minRotation: 0,
            },
          },
          y: {
            beginAtZero: true,
            maxTicksLimit: 5,
          },
        },
      },
    });
  }, [props.lineGraph]);

  const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  return <canvas ref={chartRef} style={{ width: '100%', height: '300px' }} />;
};

export default LineGraph;
