import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import mergeData from "../../json_data/merged_file.json";
import Displaytable from "../Displaytable";
import { Slider } from '@mui/material';
import LineGraph from "../Components/LineGraph";

const SearchComponent = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [sliderValue, setSliderValue] = useState(0);
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    performSearch(searchTerm, selectedDate, selectedTime);
  }, [searchTerm, selectedDate, selectedTime]);

  useEffect(() => {
    setGraphData([...searchResults.map((item) => item.thermostatPacket)]);
  }, [searchResults]);

  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleTimeChange = (e) => {
    const time = e.target.value;
    setSelectedTime(time);
  };

  const handleSliderChange = (event, value) => {
    setSliderValue(value);
  };

  const performSearch = (term, date, time) => {
    const results = mergeData.filter((item) => {
      // Filter logic remains the same
      // ...
    });

    setSearchResults(results);
  };

  return (
    <div>
      <div>
        <input
          style={{
            width: "30%",
            height: "3vh",
            padding: "5px",
            marginBottom: "5px",
          }}
          type="text"
          className="search-bar"
          placeholder="Search product"
          value={searchTerm}
          onChange={handleSearch}
        />
        <DatePicker
          style={{ width: "30%", height: "3vh" }}
          selected={selectedDate}
          onChange={handleDateChange}
          placeholderText="mm/dd/yyyy"
          dateFormat="MM/dd/yyyy"
        />
        <input
          style={{
            width: "30%",
            height: "3vh",
            padding: "5px",
            marginBottom: "5px",
          }}
          type="text"
          className="search-bar"
          placeholder="Search time (ISO format)"
          value={selectedTime}
          onChange={handleTimeChange}
        />
        <Slider
          aria-label="Temperature"
          value={sliderValue}
          min={0}
          max={100}
          onChange={handleSliderChange}
          valueLabelDisplay="auto"
        />
      </div>
      <div className="graph-container">
        <LineGraph lineGraph={graphData} />
      </div>
      <div className="table-container">
        <table className="custom_table">
          <thead>

            <tr>
//             <th>date</th>
//             <th>time</th>
//             <th>product</th>
//             <th>version</th>
//             <th>id</th>
//             <th>hvacMode</th>
//             <th>deadband10xF</th>
//             <th>outdoorTempLockout10xF</th>
//             <th>programMode</th>
//             <th>fanMode</th>
//             <th>numHeatStages</th>
//             <th>numCompressorStages</th>
//             <th>numHeatStages</th>
//             <th>smartSensorCnt</th>
//             <th>outdoorTemperature10xF</th>
//             <th>indoorTemperature10xF</th>
//             <th>outdoorHumidityPer</th>
//             <th>indoorHumidityPer</th>
//           
            </tr>
          </thead>
          <tbody>
            {searchResults.map((item, index) => (
              <Displaytable data={item} key={index} />
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SearchComponent;
