import React, { useEffect, useRef, useState } from 'react';
import { Chart } from 'chart.js/auto';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

const LineGraph = ({ lineGraph }) => {
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    setGraphData([...lineGraph]);
  }, [lineGraph]);

  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    if (Array.isArray(graphData) && graphData.length > 0) {
      const labels = graphData.map((item) => item.data.timestamp);
      const datasets = [
        {
          label: 'Data',
          data: graphData.map((item) => item.data.value),
          fill: false,
          borderColor: 'rgba(75,192,192,1)',
          tension: 0.1,
        },
      ];

      chartInstance.current = new Chart(ctx, {
        type: 'line',
        data: {
          labels: labels,
          datasets: datasets,
        },
        options: {
          maintainAspectRatio: true,
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        },
      });
    }
  }, [graphData]);

  return (
    <div>
      <canvas ref={chartRef} style={{ width: '100%', height: '200px' }} />
      <Slider
        min={new Date('2022-11-07').getTime()}
        max={new Date('2022-12-02').getTime()}
        defaultValue={new Date('2022-11-07').getTime()}
        marks={{
          [new Date('2022-11-07').getTime()]: '2022-11-07',
          [new Date('2022-12-02').getTime()]: '2022-12-02',
        }}
      />
    </div>
  );
};

export default LineGraph;
