import React, { useEffect, useRef, useState } from 'react';
import { Chart } from 'chart.js/auto';

const LineGraph = (props) => {
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    setGraphData([...props.lineGraph]);
  }, [props.lineGraph]);

  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    // Filter out incorrect or invalid dates
    const filteredData = graphData.filter((item) => {
      const date = item.data.timestamp.split('T')[0];
      return date === props.searchDate; // Replace `props.searchDate` with the actual search date from your application's state or props
    });

    const labels = filteredData.map((item) => item.data.timestamp.split('T')[0]);
    // const datasets = filteredData.map((item) => ({
    //   label: item.data.indoorTemperature10xF,
    //   data: filteredData.map((item) => item.data.indoorTemperature10xF),
    //   fill: false,
    //   borderColor: getRandomColor(),
    //   tension: 0.5,
    // }));
    const datasets = filteredData.map((item) => ({
      label: 'Indoor Temperature',
      data: filteredData.map((item) => item.data.indoorTemperature10xF),
      fill: false,
      borderColor: getRandomColor(),
      tension: 0.5,
    }));
    

    chartInstance.current = new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: datasets,
      },
      options: {
        maintainAspectRatio: false,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  }, [props.lineGraph, props.searchDate]);

  const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  return <canvas ref={chartRef} style={{ width: '10%', height: '10%' }} />;
};

export default LineGraph;
