import React, { useState, useEffect, useContext } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import mergeData from "../../json_data/merged_file.json";
import Displaytable from "../Displaytable";
import Gdata from "../GobalContext";
import { format, parseISO } from "date-fns";
import { utcToZonedTime } from "date-fns-tz";

const SearchComponent = () => {
  let gdata = useContext(Gdata);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDate, setSelectedDate] = useState(null);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    performSearch(searchTerm, selectedDate);
  }, [searchTerm, selectedDate]);

  useEffect(() => {
    gdata.update(graph);
  }, [searchResults]);

  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const performSearch = (term, date) => {
    const results = mergeData.filter((item) => {
      if (item.hasOwnProperty("thermostatPacket")) {
        const { product } = item.thermostatPacket.info;

        if (!date) {
          // When no date is selected, include all items
          return product.toLowerCase().includes(term.toLowerCase());
        } else {
          // When date is selected, filter by date
          const itemDateTime = parseISO(item.thermostatPacket.data.timestamp);
          const itemFormattedDateTime = format(itemDateTime, "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'");
          const itemZonedDateTime = utcToZonedTime(itemFormattedDateTime, "UTC");

          return (
            product.toLowerCase().includes(term.toLowerCase()) &&
            format(itemZonedDateTime, "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'") ===
              format(date, "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'")
          );
        }
      }
    });

    setSearchResults(results);
  };

  const graph = searchResults.map((item, index) => item.thermostatPacket);

  return (
    <div>
      <input
        style={{
          width: "30%",
          height: "3vh",
          padding: "5px",
          marginBottom: "5px",
        }}
        type="text"
        className="search-bar"
        placeholder="Search product"
        value={searchTerm}
        onChange={handleSearch}
      />
      <DatePicker
        style={{ width: "30%", height: "3vh" }}
        selected={selectedDate}
        onChange={handleDateChange}
        placeholderText="mm/dd/yyyy"
        dateFormat="MM/dd/yyyy"
      />

      <table className="custom_table">
        <thead>
          <tr>
            <th>date</th>
            <th>time</th>
            <th>product</th>
            <th>version</th>
            <th>id</th>
            <th>hvacMode</th>
            <th>deadband10xF</th>
            <th>outdoorTempLockout10xF</th>
            <th>programMode</th>
            <th>fanMode</th>
            <th>numHeatStages</th>
            <th>numCompressorStages</th>
            <th>numHeatStages</th>
            <th>smartSensorCnt</th>
            <th>outdoorTemperature10xF</th>
            <th>indoorTemperature10xF</th>
            <th>outdoorHumidityPer</th>
            <th>indoorHumidityPer</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((item, index) => (
            <Displaytable data={item} key={index} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SearchComponent;
