import React, { useEffect, useRef, useState } from 'react';
import { Chart } from 'chart.js/auto';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

const LineGraph = ({ lineGraph, selectedStartDate, selectedEndDate, setSelectedStartDate }) => {
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    setGraphData([...lineGraph]);
  }, [lineGraph]);

  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  let labels = graphData.map((item) => item.data.timestamp);
  let datasets = [];

  const fields = [
    { key: 'indoorTemperature10xF', label: 'Indoor Temperature' },
    { key: 'outdoorHumidityPer', label: 'Outdoor Humidity' },
    { key: 'indoorHumidityPer', label: 'Indoor Humidity' },
    { key: 'outdoorTemperature10xF', label: 'Outdoor Temperature' },
  ];

  useEffect(() => {
    const ctx = chartRef.current.getContext('2d');

    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    const filteredData = graphData.filter((item) => {
      const timestamp = item.data.timestamp;
      return timestamp >= selectedStartDate && timestamp <= selectedEndDate;
    });

    datasets = fields.map((field) => ({
      label: field.label,
      data: filteredData.map((item) => item.data[field.key]),
      fill: false,
      borderColor: getRandomColor(),
      tension: 0.1,
    }));

    chartInstance.current = new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: datasets,
      },
      options: {
        maintainAspectRatio: true,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  }, [graphData, selectedStartDate, selectedEndDate]);

  const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  return (
    <div>
      <canvas ref={chartRef} style={{ width: '100%', height: '200px' }} />
      <Slider
        min={new Date('2022-11-07').getTime()}
        max={new Date('2022-12-02').getTime()}
        defaultValue={selectedStartDate}
        value={selectedStartDate}
        onChange={(value) => {
          setSelectedStartDate(value);
        }}
        allowCross={false}
        step={24 * 60 * 60 * 1000} // 1 day in milliseconds
        marks={{
          [new Date('2022-11-07').getTime()]: '2022-11-07',
          [new Date('2022-12-02').getTime()]: '2022-12-02',
        }}
      />
    </div>
  );
};

export default LineGraph1;
